pub(crate) fn run() {
    let input = get_input();

    let final_floor: i32 = input.chars().map(|c| match c {
        '(' => 1i32,
        ')' => -1i32,
        _ => panic!("out of bounds input")
    }).sum();
    println!("Santa ends up in floor {}", final_floor);

    let mut floor = 0i32;
    let enters_basement_at = input.chars().enumerate()
        .skip_while(|(_, c)| {
            floor += match c {
                '(' => 1i32,
                ')' => -1i32,
                _ => panic!("out of bounds input")
            };
            floor != -1
        })
        .next().expect("Santa never enters the basement")
        .0 + 1;
    println!("Santa enters the basement at position {}", enters_basement_at);
}

